document.addEventListener("DOMContentLoaded",()=>{document.querySelector("main").setAttribute("tabindex","-1");document.getElementById("createEventBtn").addEventListener("click",()=>{alert("Event creation coming soon!")});const e=document.getElementById("weatherInfo");async function t(){e.innerHTML='<span class="spinner" aria-label="Loading weather"></span>';try{const t=await fetch("https://api.data.gov.sg/v1/environment/24-hour-weather-forecast");if(!t.ok)throw new Error("Network response was not ok");const a=await t.json(),n=a.items[0]?.general?.forecast||"No forecast available",r=a.items[0]?.general?.temperature?.low,o=a.items[0]?.general?.temperature?.high,c=a.items[0]?.general?.relative_humidity?.low,d=a.items[0]?.general?.relative_humidity?.high;e.innerHTML=`<div class="weather-forecast"><span aria-hidden="true">☀️</span> <strong>Forecast:</strong> ${n}<br><strong>Temperature:</strong> ${r}&deg;C – ${o}&deg;C<br><strong>Humidity:</strong> ${c}% – ${d}%</div>`}catch(t){e.innerHTML='<span class="error-message">Weather data unavailable. Please try again later.</span>'}}t();const a=document.getElementById("mapContainer");a.textContent="",document.getElementById("eventList").innerHTML="<li>World Oceans Day Cleanup - June 8, 10am, Sandy Beach</li>"});
